var timeloop = flow.get("heat_interval", "memoryOnly");
var process;

switch(global.get("process.type", "memoryOnly")){
    case "dist":
        process = global.get("process.dist", "memoryOnly");
        break;
    case "reflux":
        process = global.get("process.reflux", "memoryOnly");
        break;
    default:
        process = null;
        break;
}

var out1 = null
var out2 = { delay: 0 };
var out3 = { payload: 0 };
var percent_heat = 0;

switch(process.stage){
    case "heat":
        percent_heat = process.power.heat;
         break;
    case "work":
        percent_heat = process.power.work;
        break;
    case "head":
        percent_heat = process.power.body;
        break;
    case "stabilize":
        percent_heat = process.power.body;
        break;
    case "setpoint":
        percent_heat = process.power.body;
        break;
    case "body":
        percent_heat = process.power.body;
        break;
    default:
        percent_heat = 0
        break;
}

switch(percent_heat){
    case 0:
        out1 = null;
        out2 = { 
            delay: 0,
            flush: true
        };
        break;
    case 100:
        out1 = { payload: true };
        out2 = { 
            delay: timeloop/100*percent_heat*1000,
            reset: true
        };
        break;
    default:
        out1 = { payload: true };
        out2 = { delay: timeloop/100*percent_heat*1000};
        break;
}

out3 = { payload: percent_heat };

return [out1, out2, out3];

var current_temperature = global.get("debug.temperature.tube")
if (isNaN(current_temperature)) {current_temperature = 0}
if (msg.topic == 'sum') {
    current_temperature = current_temperature + msg.payload;
} else {
    current_temperature = current_temperature - msg.payload;
}

global.set("debug.temperature.tube", current_temperature);
msg.payload = current_temperature;
return msg;

var current_temperature = global.get("debug.temperature.chiller")
if (isNaN(current_temperature)) {current_temperature = 0}
if (msg.topic == 'sum') {
    current_temperature = current_temperature + msg.payload;
} else {
    current_temperature = current_temperature - msg.payload;
}

global.set("debug.temperature.chiller", current_temperature);
msg.payload = current_temperature;
return msg;

var current_temperature = global.get("debug.temperature.still")
if (isNaN(current_temperature)) {current_temperature = 0}
if (msg.topic == 'sum') {
    current_temperature = current_temperature + msg.payload;
} else {
    current_temperature = current_temperature - msg.payload;
}

global.set("debug.temperature.still", current_temperature);
msg.payload = current_temperature;
return msg;

var still = {};
still.options = [];
var tube = {};
tube.options = [];
var chiller = {};
chiller.options = [];
var sens_ids = global.get("sensors").split(';')
var sens_still = global.get("sensor.still",'file');
var sens_tube = global.get("sensor.tube",'file');
var sens_chiller = global.get("sensor.chiller",'file');

for (i = 0; i < sens_ids.length; i++) {
    if (sens_ids[i] != sens_chiller && sens_ids[i] != sens_tube){
        still.options.push(sens_ids[i])
    }
    if (sens_ids[i] != sens_still && sens_ids[i] != sens_tube){
        chiller.options.push(sens_ids[i])
    }
    if (sens_ids[i] != sens_still && sens_ids[i] != sens_chiller){
        tube.options.push(sens_ids[i])
    }
}
//for (const [key, value] of Object.entries(sens_ids)) {

//}

still.options.push('none')
still.payload = sens_still;

tube.options.push('none')
tube.payload = sens_tube;

chiller.options.push('none')
chiller.payload = sens_chiller;

return [still, tube, chiller];

var ratio = 0.3
var saved_sensors = global.get('sensor','file');
var fixed_topic = msg.topic.replace('pimoon/temperature/','');
var current_temp_topic = "sensor."+fixed_topic;
var prev_temp = global.get(current_temp_topic);

if (isNaN(prev_temp)){
    prev_temp=msg.payload;
}

var rounded_temperature = prev_temp*ratio+msg.payload*(1-ratio);

global.set(current_temp_topic,rounded_temperature);

switch (fixed_topic){

   case saved_sensors.still:
       if (!isNaN(global.get('debug.temperature.still'))) {
        rounded_temperature=rounded_temperature+global.get('debug.temperature.still')
       }
       global.set('temperature.still',rounded_temperature);
       msg.topic='Куб';
       msg.payload=rounded_temperature;
       return msg;

   case saved_sensors.tube:
       if (!isNaN(global.get('debug.temperature.tube'))) {
        rounded_temperature=rounded_temperature+global.get('debug.temperature.tube')
       }       
       global.set('temperature.tube',rounded_temperature);
       msg.topic='Царга';
       msg.payload=rounded_temperature;
       return msg;

   case saved_sensors.chiller:
       if (!isNaN(global.get('debug.temperature.chiller'))) {
        rounded_temperature=rounded_temperature+global.get('debug.temperature.chiller')
       }        
       global.set('temperature.chiller',rounded_temperature);
       msg.topic='Вода';
       msg.payload=rounded_temperature;
       return msg;
}

var timeloop = msg.payload;
var valve;
var out1 = { delay: 0 };
var timeout;

switch(global.get("process.reflux.stage", "memoryOnly")){
    case "head":
        valve = global.get("process.reflux.valve.head", "memoryOnly");
        timeout = valve.frequency*1000*valve.opentime/100;
        break;
    case "body":
        valve = global.get("process.reflux.valve.body", "memoryOnly");
        timeout = valve.frequency*1000*valve.opentime/100;
        break;
    default:
        valve = null;
        break;
}

out1 = { delay: timeout };

return out1;

var liquid = {};
liquid.options = [];
var alch = {};
alch.options = [];
var water = {};
water.options = [];

var sens_liquid = global.get("sensor.liquid",'file');
if (isNaN(sens_liquid)) sens_liquid = 5;
var sens_alch = global.get("sensor.alch",'file');
if (isNaN(sens_alch)) sens_alch = 5;
var sens_water = global.get("sensor.water",'file');
if (isNaN(sens_water)) sens_water = 5;

for (i = 1; i < 5; i++) {
    if (i != sens_alch && i != sens_water){
        liquid.options.push(i)
    }
    if (i != sens_liquid && i != sens_water){
        alch.options.push(i)
    }
    if (i != sens_alch && i != sens_liquid){
        water.options.push(i)
    }
}

liquid.payload = sens_liquid;
liquid.options.push({"Нет":5})
alch.payload = sens_alch;
alch.options.push({"Нет":5})
water.payload = sens_water;
water.options.push({"Нет":5})
return [liquid, alch, water];

var percentPower = msg.payload;
msg.payload =Buffer.from([0x41, 0x54, 0x2B, 0x70, 0x3D, percentPower, 0x6D+percentPower, 0x03]);
return msg;

var obj=RED.util.cloneMessage(global.get("default",'file')) || {'value' : null};
global.set ("process", obj);
return msg;

var template_pressure = "\n\n <b>    Давление:</b>\n" +
    (global.get('pressure.current')/1.334).toFixed(1) +
    " мм рт.ст.";

var template_temperature = "\n\n <b>    Температура:</b>" +
    " \n<code>Куб:   </code>" +
        parseFloat(global.get('temperature.still', "memoryOnly")).toFixed(2) +
    "\n<code>Царга: </code>"+
        parseFloat(global.get('temperature.tube', "memoryOnly")).toFixed(2) +
    "\n<code>Вода:  </code>" + 
        parseFloat(global.get('temperature.chiller', "memoryOnly")).toFixed(2);


var template_valve = "\n\n <b>    Клапаны:</b>" + "\n<code>Вода:   </code>";
if (global.get('valve.water.hand', "memoryOnly") == 'false') {
    template_valve += '🚫';
} else if (global.get('valve.water.hand', "memoryOnly") == 'true') {
    template_valve += '🟢';
} else {
    if (global.get('valve.water.on', "memoryOnly")) {
        template_valve += '🟢';
    } else {
        template_valve += '🔴';
    }
}


template_valve += "\n<code>Головы: </code>";
if (global.get('valve.heads.hand', "memoryOnly") == 'false') {
    template_valve += '🚫';
} else if (global.get('valve.heads.hand', "memoryOnly") == 'true') {
    template_valve += '🟢';
} else {
    if (global.get('valve.heads.on', "memoryOnly")) {
        template_valve += '🟢';
    } else {
        template_valve += '🔴';
    }
}
template_valve += "\n<code>Тело:   </code>";
if (global.get('valve.body.hand', "memoryOnly") == 'false') {
    template_valve += '🚫';
} else if (global.get('valve.body.hand', "memoryOnly") == 'true') {
    template_valve += '🟢';
} else {
    if (global.get('valve.body.on', "memoryOnly")) {
        template_valve += '🟢';
    } else {
        template_valve += '🔴';
    }
}


var template_safe = "\n\n <b>    Датчики:</b>" + 
        "\n<code>Тары:   </code>";

if  (global.get('safe.liquid', "memoryOnly")== '0') {
    template_safe += '🟢';
} else if (global.get('safe.liquid', "memoryOnly")== '1') {
    template_safe += '🔴';
} else {
    template_safe += '🚫';
}

var template_stage;

if (global.get('process.type') == 'dist') {
    switch (global.get('process.dist.stage')) {
        case 'heat':
            template_stage = 'Разогрев'
            break;
        case 'work':
            template_stage = 'Отбор'
            break;
        case 'cooling':
            template_stage = 'Охлаждение'
            break;
        case 'final':
            template_stage = 'Финал'
            break;
        case 'hand_finish':
            template_stage = 'Ручная остановка'
            break;
        case 'error_finish':
            template_stage = 'Остановка из-за ошибки'
            break;    
        default:
            template_stage = 'Непойми что';
            break;
    }
    msg.payload.content = "<b>Процесс:</b>   Дистилляция" + "\n<b>Стадия:</b>     " + 
        template_stage +
        template_temperature +
        template_valve + 
        template_safe +
        template_pressure;
} else if (global.get('process.type') == 'reflux') {
        var t_e = 'N/A'
        switch (global.get('process.reflux.stage')) {
        case 'heat':
            template_stage = 'Разогрев'
            break;
        case 'stabilize':
            t_e = global.get('process.reflux.t_stab_e')
            template_stage = 'Стабилизация \n<b>Осталось:</b>  ' + t_e
            break;
        case 'head':
            template_stage = 'Отбор Голов'
            break;
        case 'setpoint':
            t_e = global.get('process.reflux.t_point_e')
            template_stage = 'Уставка \n<b>Осталось:</b>  ' + t_e
            break;
        case 'body':
            template_stage = 'Отбор Тела' + '\n<b>Старт/Стопов:</b> ' + global.get('process.reflux.startstop')
            break;
        case 'cooling':
            t_e = global.get('process.reflux.t_cooling_e')
            template_stage = 'Охлаждение \n<b>Осталось:</b>  ' + t_e
            break;
        case 'final':
            template_stage = 'Финал'
            break;
        case 'hand_finish':
            template_stage = 'Ручная остановка'
            break;
        case 'error_finish':
            template_stage = 'Остановка из-за ошибки'
            break;    
        default:
            template_stage = 'Непойми что';
            break;
    }
    msg.payload.content = "<b>Процесс:</b>   Ректификация" + "\n<b>Стадия:</b>     " + 
        template_stage +
        template_temperature +
        template_valve + 
        template_safe +
        template_pressure;
} else {
    msg.payload.content = '<b>Ничего не делаем</b>' +
    template_temperature +
    template_valve + 
    template_safe +
    template_pressure;
}
msg.payload.options = {parse_mode : "HTML"}
return msg;
